           when exists(
                   select *
                   from ujcms_channel t
                   where t.article_model_id_ = #{channelId}
                      or t.channel_model_id_ = #{channelId}
               ) then 1
           else 0
           end as non_zero_exists

select case
           when exists(
                   select *
                   from ujcms_channel t
                   where t.alias_ = #{alias}
                     and t.site_id_ = #{siteId}
               ) then 1
           else 0
           end as non_zero_exists

select case when exists( select * from ujcms_role_article ra where ra.channel_id_ = #{channelId} and ra.role_id_ in
#{item}
) then 1 else 0 end as non_zero_exists
